mapper.select:
SELECT os.order_source_id, os.source_no, os.source_name, os.create_time, os.remark, os.is_stop, os.create_id, su.user_name as createName FROM t_order_source os LEFT JOIN sys_user su ON os.create_id = su.user_id WHERE os.is_deleted = 0 AND os.is_stop = 0
AND os.source_name like #{sourceName}
AND os.source_no like #{sourceNo}
