SELECT wh.warehouse_id, wh.province, wh.city, wh.district, wh.address, wh.region_id, wh.latitude, wh.longitude, wh.warehouse_no, wh.warehouse_name, wh.create_id, wh.create_time, wh.manager_name, wh.modify_id, wh.modify_time, wh.is_deleted, wh.remark, wh.mobile_phone, wh.order_source_id, wh.express_company_id, os.source_name as orderSourceName, ec.company_name as expressCompanyName FROM t_warehouse wh LEFT JOIN t_order_source os on wh.order_source_id = os.order_source_id LEFT JOIN t_express_company ec on wh.express_company_id = ec.express_company_id where 1= 1
AND wh.warehouse_id = #{map.warehouseId}
AND wh.warehouse_no = #{map.warehouseNo}

